import React, { Component } from "react";
import DogIndex from "./DogIndex";
import Favorites from "./Favorites";
import Banned from "./Banned";

class ListContainer extends Component {
  state={
    dogs: [],
    favorites: [],
    banned: [],
  }

  fetchDogs = () => {
    fetch('http://localhost:3000/dogs')
    .then(r => r.json())
    .then(data => {
      this.setState({
        dogs: data
      })
    })
  }

  componentDidMount() {
    this.fetchDogs()
  }

handleFavoritesClick = (dogObj) => {
  const favorites = [...this.state.favorites].filter(dog => dog.id === dogObj.id)
  this.setState({
    favorites
  })
}



  render() {
    return (<div className="list-container" >
      <h1>Index</h1><br />
      <DogIndex dogs={this.state.dogs} handleClick={props.handleClick}/>
      <h1>Favorites</h1>
      <Favorites dogs={this.state.favorites} handleClick={this.handleFavoritesClick}/>
      <h1>Banned</h1>
      <Banned dogs={this.state.banned}/>

    </div>)
  }
}

export default ListContainer;
